package com.devlabs.lab1;

public class ReminderNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=5;
		int num2=2;
		double reminder;
		double quotient;
		reminder=num1%num2;
		quotient=num1/num2;
		System.out.println("quotient : " + quotient);
		System.out.println("reminder : " + reminder);
	}

}
